import React from 'react';
import {
  ChakraProvider,
  Box,
  Text,
  Link,
  VStack,
  Code,
  Grid,
  theme,
} from '@chakra-ui/react';
import { ColorModeSwitcher } from './ColorModeSwitcher';

import { Line } from 'react-chartjs-2';
import { makeStyles, useTheme } from '@material-ui/core';

function App() {
  const [temperature, setTemperature] = React.useState(0)
  const [breathing, setBreathing] = React.useState([])
  const [timer, setTimer] = React.useState(null)

  // Draw a real time line chart of breathing data
  const drawChart = () => {
    const ctx = document.getElementById('breathing').getContext('2d');
    const myChart = new Chart(ctx, {
      type: 'line',
      data: {
        labels: breathing.map((b, i) => i),
        datasets: [
          {
            label: 'Breathing',
            data: breathing,
            backgroundColor: 'rgba(255, 99, 132, 0.2)',
            borderColor: 'rgba(255, 99, 132, 1)',
            borderWidth: 1,
          },
        ],
      },
      options: {
        scales: {
          yAxes: [
            {
              ticks: {
                beginAtZero: true,
              },
            },
          ],
        },
      },
    });

    return myChart;
  };

  async function getData() {
    try {
      const result = await fetch('http://localhost:5000/app')
      const data = await result.json()
      data.forEach(function (item, index) {
        if (item.type === 'Temperature') {
          setTemperature(item.value)
        } else if (item.type === 'Breathing') {
          setBreathing(breathing => [...breathing, item.value])
        }
      });
    } catch (e) {
      console.error(e)
    }

    clearTimeout(timer)
    setTimer(setTimeout(getData, 500))
  }

  React.useEffect(() => {
    getData();
    drawChart();
  }, []);

  return (
    <ChakraProvider theme={theme}>
      <Box textAlign="center" fontSize="xl">
        <Grid minH="100vh" p={3}>
          <ColorModeSwitcher justifySelf="flex-end" />
          <VStack spacing={8}>
            <p>Temperature: {temperature}</p>
            {/* <p>Breathing: {breathing}</p>s */}
            <canvas id="breathing"></canvas>
          </VStack>
        </Grid>
      </Box>
    </ChakraProvider>
  );
}

export default App;
