import React from 'react';
import {
  ChakraProvider,
  Box,
  Text,
  Link,
  VStack,
  Code,
  Grid,
  theme,
} from '@chakra-ui/react';
import { ColorModeSwitcher } from './ColorModeSwitcher';

import { Line } from 'react-chartjs-2';
import 'chartjs-plugin-streaming';
import { makeStyles, useTheme } from '@material-ui/core';
var createReactClass = require('create-react-class');


function App() {
  const [temperature, setTemperature] = React.useState(0)
  const [breathing, setBreathing] = React.useState([])
  const [timer, setTimer] = React.useState(null)

  const data = {
    datasets: [
      {
        label: "Breathing",
        borderColor: "rgb(255, 99, 132)",
        backgroundColor: "rgba(255, 99, 132, 0.5)",
        lineTension: 0,
        borderDash: [8, 4],
        data: []
      }
    ]
  };

  const options = {
    scales: {
      xAxes: [
        {
          type: "realtime",
          realtime: {
            onRefresh: function() {
              data.datasets[0].data.push({
                x: Date.now(),
                y: Math.random() * 100
              });
            },
            delay: 2000
          }
        }
      ]
    }
  };

  async function getData() {
    try {
      const result = await fetch('http://localhost:5000/app')
      const data = await result.json()
      data.forEach(function (item, index) {
        if (item.type === 'Temperature') {
          setTemperature(item.value)
        } else if (item.type === 'Breathing') {
          setBreathing(breathing => [...breathing, item.value])
        }
      });
    } catch (e) {
      console.error(e)
    }

    clearTimeout(timer)
    setTimer(setTimeout(getData, 500))
  }

  React.useEffect(() => {
    getData();
  }, []);

  return (
    <ChakraProvider theme={theme}>
      <Box textAlign="center" fontSize="xl">
        <Grid minH="100vh" p={3}>
          <ColorModeSwitcher justifySelf="flex-end" />
          <VStack spacing={8}>
            <p>Temperature: {temperature}</p>
            {/* <p>Breathing: {breathing}</p>s */}
            <Line
              datasetIdKey='id'
              data={{
                labels: ['Jun', 'Jul', 'Aug'],
                datasets: [
                  {
                    id: 1,
                    label: '',
                    data: [5, 6, 7],
                  },
                  {
                    id: 2,
                    label: '',
                    data: [3, 2, 1],
                  },
                ],
              }}
            />
          </VStack>
        </Grid>
      </Box>
    </ChakraProvider>
  );
}

export default App;
